import React, {Component} from 'react';
import '../stylesheets/home.css';
import {getTasks} from "../api"
import List from "../components/List.js"
import { connect } from 'react-redux';
import { changeList } from '../actions'
import store from "../store";



function ChangeList(i) {
    // getTasks()
    store.dispatch({type: "CHANGE_LIST", text: i})
}

function TaskLists(ToDoList, dispatch) {
    console.log(ToDoList)
    console.log(dispatch)
    return ToDoList.map(i =>
        <a onClick={() => {ChangeList(i)}}>{i}</a>
    )
}


// App.js
function Home(props) {
        console.log(this.props)
        return (
            <div className="home">
                <div className="sidebar">
                    <h1>Task Lists</h1>
                    {TaskLists(this.props.ToDoList, this.props.changeList)}
                </div>
                <div className="content">
                    <h1>{store.getState().CurrentList}</h1>
                    <h1>{this.props.CurrentList}</h1>
                    {/*<List state={state} CurrentList={state.CurrentList}></List>*/}
                </div>
            </div>
        );
}

// AppContainer.js
const mapStateToProps = state => ({
    CurrentList: state.CurrentList,
    ToDoList: state.ToDoList
});

const mapDispatchToProps = {
    changeList
};

const HomeContainer = connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);

export default HomeContainer;
