import React, {Component} from 'react';
import '../stylesheets/home.css';
import {getTasks} from "../api"
import List from "../components/List.js"
import { connect } from 'react-redux';
import { changeList } from '../actions'
import store from "../store";
import { genFields } from '../services/index.js'



// function ChangeList(i) {
//     // getTasks()
//     store.dispatch({type: "CHANGE_LIST", text: i})
// }

function random() {
    return Math.random();
}

function Teams(mlbData) {
    console.log(mlbData)
    return mlbData.stats.map(i =>
        <div key={random()} className="Column">
            <div className="ColumnHeader">
                {i}
            </div>
            {
                mlbData.info.map(y =>
                    <div key={random()} className="Field">
                        {
                            (i == "officialLogoImageSrc") ? <img className="Image" src={y[i]}/> : y[i]
                        }
                    </div>
                )
            }
        </div>
    )
}

function ChangeList(newList) {
    store.dispatch({type: "CHANGE_LIST", text: newList})
}


// App.js
function Home(props) {

        console.log(store.getState())
        console.log(props.mlbStats)
        return (
            <div className="home">
                <div className="header">
               </div>
                <div className="customContainer">
                    <div className="contentHeader">
                        <button onClick={() => {ChangeList("OverAll")}} className="contentButton">Over View</button>
                        <button onClick={() => {ChangeList("Batting")}} className="contentButton">Batting</button>
                        <button onClick={() => {ChangeList("Pitching")}} className="contentButton">Pitching</button>
                        <button onClick={() => {ChangeList("Fielding")}} className="contentButton">Fielding</button>
                    </div>
                    <div className="contentFilter">

                    </div>
                    <div className="ColumnContainer">
                        {Teams(genFields(props.mlbStats, props.CurrentList))}
                    </div>
                </div>
            </div>
        );
}

// AppContainer.js
const mapStateToProps = state => ({
    mlbStats: state.mlb_stats,
    CurrentList: state.CurrentList
    // ToDoList: state.ToDoList
});

const mapDispatchToProps = {
    // changeList
};

const HomeContainer = connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);

export default HomeContainer;
