import { createStore } from 'redux'

function todos(state = {}, action) {
    switch (action.type) {
        case 'UPDATE_STATE':
            return {
                ...state,
                mlb_stats: state.mlb_stats.concat(action.state.mlb_stats)
            }
        case 'CHANGE_LIST':
            return {
                ...state,
                CurrentList: action.text
            }
        case 'UPDATE_AVG':
            if (state.FormWins === 0) return {
                ...state,
                FormAvg: 0
            }
            return {
                ...state,
                FormAvg: (state.FormWins / (state.FormWins + state.FormLosses)).toFixed(3)
            }
        case 'UPDATE_WINS':
            return {
                ...state,
                FormWins: action.text
            }
        case 'UPDATE_LOSSES':
            return {
                ...state,
                FormLosses: action.text
            }
        default:
            return state
    }
}
let store = createStore(todos, {
    CurrentList: "OverAll",
    CurrentSort: "city",
    FormAvg: 0,
    FormWins: 0,
    FormLosses: 0
})





export default store
