import { createStore } from 'redux'

function todos(state = {}, action) {
    switch (action.type) {
        case 'ADD_TODO':
            return state.todos.concat([action.text])
        case 'CHANGE_LIST':
            return state.CurrentList = action.text
        default:
            return state
    }
}

const store = createStore(todos, {
    todos:["HIYA", "HELLLOOOO"],
    ToDoLists:["ONE", "TWO"],
    CurrentList: "One"
})

export default store
