import store from './store.js'


export function getTasks() {
    fetch('/Teams').then((response) => {
        return response.json();
    }).then((data) => {
        console.log(data._embedded)
        store.dispatch({type: "UPDATE_STATE", state: data._embedded})
    })
}
export function postTeam(json) {
    console.log(json)
    fetch("/Teams", {
        method: "POST",
        body: json,
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    }).then((response) => {
        return response.json();
    }).then((data) => {
        console.log(data)
    })

}
export function deleteTask(id) {
    fetch("/Teams/"+ id, {
        method: "DELETE",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
