import React, {Component} from 'react';
import '../stylesheets/home.css';
import {getTasks} from "../api"
import List from "../components/List.js"
import { connect } from 'react-redux';
import { changeList } from '../actions'
import store from "../store";



function ChangeList(i) {
    // getTasks()
    store.dispatch({type: "CHANGE_LIST", text: i})
}

function TaskLists(ToDoList, dispatch) {
    console.log(ToDoList)
    return ToDoList.map(i =>
        <li key={i}>{i}</li>
    )
}


// App.js
function Home(props) {
        console.log(props)
        return (
            <div className="home">
                <div className="sidebar">
                    <h1>Fillters</h1>
                </div>
                <div className="content">
                    <h1>{store.getState().CurrentList}</h1>
                    <h1>{props.CurrentList}</h1>
                    <ol>
                        {TaskLists(props.ToDoList, props.changeList)}
                    </ol>
                </div>
            </div>
        );
}

// AppContainer.js
const mapStateToProps = state => ({
    CurrentList: state.CurrentList,
    ToDoList: state.ToDoList
});

const mapDispatchToProps = {
    changeList
};

const HomeContainer = connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);

export default HomeContainer;
