import React from 'react';
import '../stylesheets/home.css';
import store from "../store";
import List from "../components/List.js"


function ChangeListAction(text) {
    return {
        type: 'CHANGE_LIST',
        text
    }
}

const ChangeList = (e, i, store) => {
    console.log(store.getState().CurrentList)
    store.dispatch(ChangeListAction(i))
    console.log(store.getState().CurrentList)
}

const TaskLists = (store) =>
    store.getState().ToDoList.map(i =>
    <a onClick={(e) => ChangeList(e, i, store)}>{i}</a>
)


function Home(props) {
    console.log(props)
    const state = props.store.getState()
    return (
    <div className="home">
        <div className="sidebar">
            <h1 style={"margin-left: auto;"}>Task Lists</h1>
            {TaskLists(props.store)}
        </div>
        <div className="content">
            <List store={props.store} CurrentList={state.CurrentList}></List>
        </div>
    </div>
    );
}

export default Home;
