import { createStore } from 'redux'

function todos(state = {}, action) {
    switch (action.type) {
        case 'UPDATE_STATE':
            return action.state
        case 'CHANGE_LIST':
            state.CurrentList = action.text
            return state
        default:
            return state
    }
}
let store = createStore(todos, {
    ToDoObject:{
        "ONE": ["HIYA", "HELLLOOOO"],
        "TWO": ["BYEYA", "GOODBYEE"]
    },
    ToDoList:["ONE", "TWO"],
    CurrentList: "TWO"
})
var getTasks = () => {
    fetch('/Tasks').then((response) => { return response.json(); }).then((data) => {
        console.log(data);
        let obj = {}
        let arr = []
        const Tasks = data._embedded.Tasks.map(task => {
            console.log(task)
            obj[task.name] = task.tasks
            arr.concat(task.name)
            console.log(obj)
        })
        const newstate = createStore(todos, {
            ToDoObject: obj,
            ToDoList: arr,
            CurrentList: arr[0]
        })
        store.dispatch({type: "UPDATE_STATE", state: newstate})
    });
}
getTasks()


export default store
