import React from 'react';
import '../stylesheets/home.css';
import store from "../store";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";
import List from "../components/List.js"


// THIS IS THE HOME PAGE DISPLAYS ALL LISTS


console.log(store.getState().ToDoLists)

const Tasks = (store, list) => store.getState()[list].map(i =>
    <li>{i}</li>
)

const Change = {type: "CHANGE_LIST",
    i
}


function ChangeList(i) {
    dispatch(Change)
}

function TaskLists() {
    store.getState().ToDoLists.map(i =>
    <Dropdown.Item onClick={ChangeList(i)}>{i}</Dropdown.Item>
)}


function Home() {
    return (
        <div className="home">
    <div className="sidebar">
        <h1>Home</h1>
            <DropdownButton title="Dropdown button">
                {TaskLists()}
            </DropdownButton>

    </div>

    <div className="content">
        <List list={store.getState().CurrentList}>

        </List>
    </div>

        </div>
    );
}

export default Home;
