import React, {Component} from 'react';
import '../stylesheets/home.css';
import {getTasks} from "../api"
import List from "../components/List.js"
import { connect } from 'react-redux'
import { changeListAction } from '../actions'


function ChangeList(e, i, state, dispatch) {
    console.log(dispatch)
    dispatch(changeListAction(i))
    console.log(state)
    getTasks()
    console.log(state)
}

function TaskLists(state, dispatch) {
    console.log(state)
    console.log(dispatch)
    return state.ToDoList.map(i =>
        <a onClick={(e) => {ChangeList(e, i, state, dispatch)}}>{i}</a>
    )
}

class Home extends Component {
    render() {
        const {state, changeList} = this.props;
        return (
            <div className="home">
                <div className="sidebar">
                    <h1>Task Lists</h1>
                    {TaskLists(state, changeList)}
                </div>
                <div className="content">
                    <List state={state} CurrentList={state.CurrentList}></List>
                </div>
            </div>
        );
    }
}
export default Home;
