import { createStore } from 'redux'

function todos(state = {}, action) {
    switch (action.type) {
        case 'ADD_TODO':
            return state.todos.concat([action.text])
        case 'CHANGE_LIST':
            return state.CurrentList = action.text
        default:
            return state
    }
}

const store = createStore(todos, {
    todos:{
        "ONE": ["HIYA", "HELLLOOOO"],
        "TWO": ["BYEYA", "GOODBYEE"]
},
    ToDoLists:["ONE", "TWO"],
    CurrentList: "ONE"
})


export default store
