import store from './store.js'


export function getTasks() {
    fetch('/Tasks').then((response) => {
        return response.json();
    }).then((data) => {
        console.log(data)
        store.dispatch({type: "UPDATE_STATE", state: data})
    })
}
export function postTasks(json) {
    fetch("/Tasks", {
        method: "POST",
        body: "{{\"city\": \"fun\"},{\"gamesPlayed\": 10}}",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
export function deleteTask(id) {
    fetch("/Tasks/"+ id, {
        method: "DELETE",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
