import React from 'react';
import '../stylesheets/home.css';
import store from "../store";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";
import List from "../components/List.js"


function ChangeListAction(text) {
    return {
        type: 'CHANGE_LIST',
        text
    }
}

const ChangeList = (e, i, store) => {
    console.log(store.getState().CurrentList)
    store.dispatch(ChangeListAction(i))
    console.log(store.getState().CurrentList)
}

const TaskLists = (store) =>
    store.getState().ToDoList.map(i =>
    <Dropdown.Item onClick={(e) => ChangeList(e, i, store)}>{i}</Dropdown.Item>
)


function Home(props) {
    return (
        <div className="home">
    <div className="sidebar">
        <h1>Home</h1>
            <DropdownButton title="Dropdown button">
                {TaskLists(props.store)}
            </DropdownButton>

    </div>

    <div className="content">
        <List list={this.props.store}>

        </List>
    </div>

        </div>
    );
}

export default Home;
