import { createStore } from 'redux'

function todos(state = {}, action) {
    switch (action.type) {
        case 'CLEAR_MLB_STATS':
            return {
                ...state,
                mlb_stats: []
            }
        case 'ADD_MLB_STATS':
            return {
                ...state,
                mlb_stats: state.mlb_stats.concat(action.state.mlb_stats),
            }
        case 'CHANGE_SORT':
            return {
                ...state,
                sort_name: action.sortName
            }
        case 'CHANGE_LIST':
            return {
                ...state,
                CurrentList: action.text
            }
        case 'UPDATE_AVG':
            if (state.FormWins === 0) return {
                ...state,
                FormAvg: 0
            }
            return {
                ...state,
                FormAvg: (state.FormWins / (state.FormWins + state.FormLosses)).toFixed(3)
            }
        case 'UPDATE_SEARCH_BAR':
            return {
                ...state,
                search_bar: action.text
            }
        case 'UPDATE_SEARCH_SECTION':
            return {
                ...state,
                search_section: action.text
            }
        case 'UPDATE_FILTER_BAR':
            return {
                ...state,
                filter_bar: action.text
            }
        case 'UPDATE_FILTER_SECTION':
            return {
                ...state,
                filter_section: action.text
            }
        case 'UPDATE_WINS':
            return {
                ...state,
                FormWins: action.text
            }
        case 'UPDATE_LOSSES':
            return {
                ...state,
                FormLosses: action.text
            }
        case 'UPDATE_OPPERATOR':
            return {
                ...state,
                opperator: action.text
            }
        default:
            return state
    }
}
let store = createStore(todos, {
    mlb_stats: [],
    filter_bar: "",
    filter_section: "wins",
    opperator: ">",
    search_bar: "",
    search_section: "city",
    sort_name: "city",
    CurrentList: "Standings",
    FormAvg: 0,
    FormWins: 0,
    FormLosses: 0
})





export default store
