import React from 'react';
import '../stylesheets/home.css';
import { connect } from 'react-redux';
import store from "../store";
import { genFields } from '../services/index.js'
import { useHistory } from "react-router-dom";
import { deleteTeam } from '../api'




// function ChangeList(i) {
//     // getTasks()
//     store.dispatch({type: "CHANGE_LIST", text: i})
// }

function random() {
    return Math.random();
}

function ChangeSort(i) {
    store.dispatch({type: "CHANGE_SORT", sortName: i})
}



function Teams(mlbData, sortName) {
    return mlbData.stats.map(i => {
        let counter = 1
        return (
            <div key={random()} className="Column">
                <button onClick={() => {ChangeSort(i)}} className={"contentButton " + (sortName === i ? "active": "")}>
                    {i}
                </button>
                {
                    mlbData.info.map(y => {
                        counter++
                        return (
                            <div key={random()} className="Field" style={(counter % 2 === 0 ? {backgroundColor: "gray"} : {} )}>
                                {
                                    (i === "officialLogoImageSrc") ? <img alt="" className="Image" src={y[i]}/> : y[i]
                                }
                            </div>
                        )
                    })
                }
            </div>
        )
    })
}

function ChangeList(newList) {
    store.dispatch({type: "CHANGE_LIST", text: newList})
}

function Delete(e, TeamObj) {
    deleteTeam(TeamObj._links.self.href.replace("http://localhost:9000", ""))
}

function Filter(state, filterObj) {
    if (state === [] || filterObj === {}) return;
    store.dispatch({type: "FILTER_STATS", state: state})
}
function Sort(state, sortName) {
    if (state === []) return;
    state.sort()
    store.dispatch({type: "SORT_STATS", state: state})
}

// App.js
function Home(props) {
        let history = useHistory();
        Filter(props.mlbStats, props.filterObj)
        Sort(props.filteredStats, props.sortName)
        return (
            <div className="home">
                <div className="header">
               </div>
                <button onClick={() => {history.push('/CreateTeam')}} className="formButton">Create Team</button>
                <div className="customContainer">
                    <div className="contentHeader">
                        <button onClick={() => {ChangeList("OverAll")}} className={"contentButton " + (props.CurrentList === "OverAll" ? "active": "")}>Over View</button>
                        <button onClick={() => {ChangeList("Batting")}} className={"contentButton " + (props.CurrentList === "Batting" ? "active": "")}>Batting</button>
                        <button onClick={() => {ChangeList("Pitching")}} className={"contentButton " + (props.CurrentList === "Pitching" ? "active": "")}>Pitching</button>
                        <button onClick={() => {ChangeList("Fielding")}} className={"contentButton " + (props.CurrentList === "Fielding" ? "active": "")}>Fielding</button>
                    </div>
                    <div className="contentFilter">

                    </div>
                    <div className="ColumnContainer">
                        {(props.sortedStats.length > 0) ? Teams(genFields(props.sortedStats, props.CurrentList), props.sortName) : null}
                        <div className="Column">
                            <div className="ColumnHeader">
                                Delete Team
                            </div>
                            {
                                props.sortedStats.map( TeamObj =>{
                                    return (
                                        <div key={random()} className="Field">
                                            <i onClick={(e) => {Delete(e, TeamObj)}} className="fa fa-trash fa-2x"></i>
                                        </div>
                                        )
                                    })
                            }
                        </div>
                    </div>
                </div>
            </div>
        );
}

const mapStateToProps = state => ({
    mlbStats: state.mlb_stats,
    sortedStats: state.sorted_stats,
    filteredStats: state.filtered_stats,
    sortName: state.sort_name,
    filterObj: state.filter_obj,
    CurrentList: state.CurrentList,
});

const mapDispatchToProps = {
};

const HomeContainer = connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);

export default HomeContainer;
