import React from 'react';
import '../stylesheets/home.css';
import {getTasks} from "../api"
import List from "../components/List.js"


function ChangeListAction(text) {
    return {
        type: 'CHANGE_LIST',
        text
    }
}

const ChangeList = (e, i, store) => {
    // console.log(store.getState().CurrentList)
    // store.dispatch(ChangeListAction(i))
    // console.log(store.getState().CurrentList)
    getTasks()
}

const TaskLists = (store) =>
    store.getState().ToDoList.map(i =>
    <a onClick={(e) => ChangeList(e, i, store)}>{i}</a>
)

const Home = ({ store }) => {
    return (
        <div className="home">
            <div className="sidebar">
                <h1>Task Lists</h1>
                {TaskLists(store)}
            </div>
            <div className="content">
                <List store={store} CurrentList={store.getState().CurrentList}></List>
            </div>
        </div>
    );
}

export default connect()(Home);
