import store from './store.js'


export function getTasks() {
    fetch('/Tasks').then((response) => {
        return response.json();
    }).then((data) => {
        let obj = {}
        let arr = []
        const Tasks = data._embedded.Tasks.map(task => {
            obj[task.name] = task.tasks
            arr = arr.concat(task.name)
        })
        const newstate = {
            ToDoObject: obj,
            ToDoList: arr,
            CurrentList: arr[0]
        }
        store.dispatch({type: "UPDATE_STATE", state: newstate})
    })
}
export function postTasks(json) {
    fetch("/Tasks", {
        method: "POST",
        body: "{{\"city\": \"fun\"},{\"gamesPlayed\": 10}}",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
export function deleteTask(id) {
    fetch("/Tasks/"+ id, {
        method: "DELETE",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
