import store from './store.js'

export function getAllTeams() {
    store.dispatch({type: "CLEAR_MLB_STATS"})
    fetch('/Teams').then((response) => {
        return response.json();
    }).then((data) => {
        data.page.totalPages.map(pageNum => {
            getTeam(pageNum)
        })
    })
}

function getTeam(pagenum = 0) {
    fetch('/Teams?page='+ pagenum).then((response) => {
        return response.json();
    }).then((data) => {
        store.dispatch({type: "ADD_MLB_STATS", state: data._embedded})
    })
}
export function postTeam(json) {
    console.log(json)
    fetch("/Teams", {
        method: "POST",
        body: JSON.stringify(json),
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
    getAllTeams()
}
export function deleteTask(id) {
    fetch("/Teams/"+ id, {
        method: "DELETE",
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
