import { createStore } from 'redux'
import {getTasks} from "./api";

function todos(state = {}, action) {
    switch (action.type) {
        case 'UPDATE_STATE':
            return {
                ...state,
                mlb_stats: action.state.mlb_stats
            }
        case 'CHANGE_LIST':
            return {
                ...state,
                CurrentList: action.text
            }
        case 'UPDATE_AVG':
            return {
                ...state,
                FormAvg: action.AVG
            }
        default:
            return state
    }
}
let store = createStore(todos, {
    CurrentList: "OverAll",
    CurrentSort: "city",
    FormAvg: 0
})

getTasks()




export default store
