import React from 'react';
import '../stylesheets/home.css';
import { connect } from 'react-redux';
import store from "../store";
import { genFields } from '../services/index.js'



// function ChangeList(i) {
//     // getTasks()
//     store.dispatch({type: "CHANGE_LIST", text: i})
// }

function random() {
    return Math.random();
}

function Teams(mlbData) {
    return mlbData.stats.map(i =>
        <div key={random()} className="Column">
            <div className="ColumnHeader">
                {i}
            </div>
            {
                mlbData.info.map(y =>
                    <div key={random()} className="Field">
                        {
                            (i === "officialLogoImageSrc") ? <img alt="" className="Image" src={y[i]}/> : y[i]
                        }
                    </div>
                )
            }
        </div>
    )
}

function ChangeList(newList) {
    store.dispatch({type: "CHANGE_LIST", text: newList})
}


// App.js
function Home(props) {
        return (
            <div className="home">
                <div className="header">
               </div>
                <div className="customContainer">
                    <div className="contentHeader">
                        <button onClick={() => {ChangeList("OverAll")}} className={"contentButton " + (props.CurrentList === "OverAll" ? "active": "")}>Over View</button>
                        <button onClick={() => {ChangeList("Batting")}} className={"contentButton " + (props.CurrentList === "OverAll" ? "active": "")}>Batting</button>
                        <button onClick={() => {ChangeList("Pitching")}} className={"contentButton " + (props.CurrentList === "OverAll" ? "active": "")}>Pitching</button>
                        <button onClick={() => {ChangeList("Fielding")}} className={"contentButton " + (props.CurrentList === "OverAll" ? "active": "")}>Fielding</button>
                    </div>
                    <div className="contentFilter">

                    </div>
                    <div className="ColumnContainer">
                        {Teams(genFields(props.mlbStats, props.CurrentList))}
                    </div>
                </div>
            </div>
        );
}

const mapStateToProps = state => ({
    mlbStats: state.mlb_stats,
    CurrentList: state.CurrentList
});

const mapDispatchToProps = {
};

const HomeContainer = connect(
    mapStateToProps,
    mapDispatchToProps
)(Home);

export default HomeContainer;
