import { createStore } from 'redux'
import {getTasks} from "./api";

function todos(state = {}, action) {
    switch (action.type) {
        case 'UPDATE_STATE':
            return {
                ...state,
                mlbStats: action.state
            }
        case 'CHANGE_LIST':
            return {
                ...state,
                CurrentList: action.text
            }
        default:
            return state
    }
}
let store = createStore(todos, {
    ToDoObject:{
        "ONE": ["HIYA", "HELLLOOOO"],
        "TWO": ["BYEYA", "GOODBYEE"]
    },
    ToDoList:["ONE", "TWO"],
    CurrentList: "TWO"
})

getTasks()




export default store
